
public class Hello9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, b = 5,c = 3;
		
		a = b+c;
		System.out.printf("%d + %d = %d\n", b, c, a);
		a = b-c;
		System.out.printf("%d - %d = %d\n", b, c, a);
		a = b*c;
		System.out.printf("%d * %d = %d\n", b, c, a);
		a = b/c;
		System.out.printf("%d / %d = %d\n", b, c, a);
		a = b%c;
		System.out.printf("%d %% %d = %d\n", b, c, a);

	}

}
